static sem_t empty_count;

#define SIZE_OF_BUFFER 20
#define DATA_LENGTH 1024


char data0[DATA_LENGTH] = "\n";
char data1[DATA_LENGTH] = "\n";
char data2[DATA_LENGTH] = "\n";
char data3[DATA_LENGTH] = "\n";
