char data9[DATA_LENGTH] = "\n";
char* dataArr[] = {data0, data1, data2, data3, data4, data5, data6, data7, data8, data9};
int enqueue = 0;
int dequeue = 0;


long init_buffer_421(void) {
	// Write your code to initialize buffer
	buffer = malloc(sizeof(struct bb_buffer_421));
	buffer->length = 0;
	buffer->read = malloc(sizeof(struct bb_node_421));
	buffer->read->data[1024] = data0;	
	buffer->read->next = NULL;
	buffer->write = NULL;
	
	struct bb_node_421 *temp = buffer->read;
	for (int i = 0; i < 20; i++){
		struct bb_node_421 *newNode_421 = malloc(sizeof(struct bb_node_421));
		newNode_421->data[1024] = data0;
		newNode_421->next = NULL;
		
		temp->next = newNode_421;
		temp = temp->next;
	}	
	temp->next = buffer->read;
	buffer->write = buffer->read;
	return 0;

	// Initialize your semaphores here.
	sem_init(&mutex, 0, 1);
	sem_init(&fill_count, 0, 0);
	sem_init(&empty_count, 0, SIZE_OF_BUFFER);
	
	return 0;
}


long enqueue_buffer_421(char * data) {
	// Write your code to enqueue data into the buffer

	if (buffer == NULL){
		return -1;
	}
	
	if (buffer->length >= 20){
		printf("failed to insert");
		return -1;
	}
	//buffer->write->data = memcpy(data);
	memcpy(buffer->write->data, data, 1024);
	enqueue += 1;
	printf("%d \n", enqueue);
	buffer->length += 1;
	buffer->write = buffer->write->next;
	
	return 0;
}

long dequeue_buffer_421(char * data) {

	// Write your code to dequeue data from the buffer
	
	if (buffer == NULL){
		return -1;
	}
	
	if (buffer->length >= 20){
		printf("failed to insert");
		return -1;
	}

	memcpy(buffer->write->data, data0, 1024);
	dequeue += 1;
	printf("%d \n", dequeue);
	buffer->length -= 1;
	buffer->write = buffer->write->next;
	
	dequeue += 1;
	return 0;
}

void *producer(void *param){
	char* item;
	int i = 0;

	while(1) {
		sleep(10);
		sem_wait(&fill_count);
		//pthread_mutex_lock(&mutex);
		sem_wait(&mutex);
		
		item = dataArr[i];
		enqueue_buffer_421(item);
		i += 1;
		if (i > 9){
			i = 0;
		}

		printf("Producer %u produced %s \n", (unsigned int)pthread_self(), item);

		//pthread_mutex_unlock(&mutex);
		sem_post(&mutex);
		sem_post(&empty_count);
	}
}

void *consumer(void *param){
	char* item;

	while(1){
		sleep(10);
		sem_wait(&empty_count);
		//pthread_mutex_lock(&mutex);
		sem_wait(&mutex);
		

		dequeue_buffer_421(&item);
		printf("Consumer %u consumed %s \n", (unsigned int)pthread_self(), item);

		//pthread_mutex_unlock(&mutex);
		sem_post(&mutex);
		sem_post(&fill_count);
	}
}


